WITH subquery AS ( SELECT reductionkey, type, max(updated) AS updated, string_agg(body, E'

Merged by database installer - ' || to_char(current_timestamp, 'YYYY-MM-DD') || E':
') AS body, count(*) FROM memos WHERE reductionkey IS NOT NULL GROUP BY reductionkey, type HAVING count(*) > 1 ) UPDATE memos SET body = subquery.body FROM subquery WHERE memos.reductionkey = subquery.reductionkey AND memos.type = subquery.type AND memos.updated = subquery.updated;
