changeSet.createProcedure:
CREATE OR REPLACE FUNCTION getManagedServiceCountForIntf(integer,varchar(16))
        RETURNS float8 AS '
   DECLARE
        nid ALIAS FOR $1;
        ipid ALIAS FOR $2;
        orec RECORD;
        counter float8;
   BEGIN
        counter = 0;
        FOR orec IN SELECT DISTINCT ifservices.ipInterfaceId, ifservices.serviceid
                FROM ifservices, ipinterface, node 
                WHERE ifservices.ipInterfaceId = ipInterface.id 
                        AND ipinterface.nodeid = node.nodeid 
                        AND ifservices.status = ''A'' 
                        AND ipinterface.ismanaged = ''M'' 
                        AND ipinterface.ipaddr = ipid 
                        AND node.nodeid = nid 
                        AND node.nodetype = ''A''
        LOOP
                BEGIN
                        counter := counter + 1;
                END;
        END LOOP;
        RETURN counter;
   END;
' LANGUAGE 'plpgsql';
