// ************** LOAD AND INITIALIZE THE MAP ********************
function load(evt)
{
	//reference to svgdocument of the map (the global variable mapSvgDocument must be defined 
	//into the page that contains the visualization of the maps applications
	mapSvgDocument=htmldocument.embeds[0].getSVGDocument();
	menuSvgDocument=htmldocument.embeds[1].getSVGDocument();
	mapContextMenu=contextMenu;
	map = new Map("#"+DEFAULT_BG_COLOR, "", "Background", htmldocument.embeds[0].width, htmldocument.embeds[0].height, 0, 0);
	var node = mapSvgDocument.getElementById("Application");
	node.appendChild(map.getSvgNode());
	node = map.getSvgNode();

	if (mapToOpen > 0){
		openMap(mapToOpen);
	}

	startRefreshNodesTime();
}


	var begin;
	var actual;
	var reloadseconds=0;
	var secondsSinceBegin=0; 

	function startRefreshNodesTime() { 
		refreshingMapElems=false;
		begin=(new Date()).getTime();
		countdown(); 
	} 

	function countdown() {
            if (isAdminMode) {
                    window.status="Admin Mode - Stop Refresh";
            		var timer=setTimeout("countdown()",1000);
            		begin=begin+1000;
            } else {
                   	actual= (new Date()).getTime();
                   	secondsSinceBegin=(actual-begin)/1000;
            		if (refreshNodesIntervalInSec>=secondsSinceBegin) {
                    	reloadseconds=Math.round(refreshNodesIntervalInSec-secondsSinceBegin);
            			window.status="Next Nodes Refresh: "+((reloadseconds-(reloadseconds%60))/(60))+ "'."+(reloadseconds%60)+"''";
            			var timer=setTimeout("countdown()",1000);
                    }else {
	                    window.status="";
	                    RefreshNodes();
		            }
            } 
    }
